CustomJavaScriptEntry.Script:
const script = document.createElement("script");
script.src = `https://cdn.jsdelivr.net/gh/ranaldsgift/KefinTweaks@latest/kefinTweaks-plugin.js`;
script.async = true;
document.head.appendChild(script);

(function () {
  const buildTarget = () => `${window.location.protocol}//${window.location.hostname}:5055`;
  const rewriteLinks = () => {
    document.querySelectorAll('a[href*="jellyseerr:5055"]').forEach((link) => {
      const updated = link.href.replace(/https?:\/\/jellyseerr:5055/gi, buildTarget());
      if (updated !== link.href) {
        link.href = updated;
      }
    });
  };
  const observer = new MutationObserver(rewriteLinks);
  observer.observe(document.body, { childList: true, subtree: true });
  rewriteLinks();
})();

(function(){
  const buildUrl = () => `${window.location.protocol}//${window.location.hostname}:5055`;
  const insertTab = () => {
    const nav = document.querySelector('.navbar-nav') || document.querySelector('.topbar-nav') || document.querySelector('ul.nav');
    if (!nav) return;
    if (nav.querySelector('a[data-injected="requests"]')) return;
    const li = document.createElement('li');
    li.className = 'nav-item';
    const a = document.createElement('a');
    a.className = 'nav-link';
    a.href = buildUrl();
    a.textContent = 'Requests';
    a.target = '_blank';
    a.setAttribute('data-injected','requests');
    li.appendChild(a);
    nav.appendChild(li);
  };
  const observer = new MutationObserver(insertTab);
  observer.observe(document.body, { childList: true, subtree: true });
  insertTab();
})();
