CustomJavaScriptEntry.Script:
const script = document.createElement("script");
script.src = `https://cdn.jsdelivr.net/gh/ranaldsgift/KefinTweaks@latest/kefinTweaks-plugin.js`;
script.async = true;
document.head.appendChild(script);

(function () {
  const buildTarget = () => `${window.location.protocol}//${window.location.hostname}:5055`;
  const rewriteLinks = () => {
    document.querySelectorAll('a[href*="jellyseerr:5055"]').forEach((link) => {
      const updated = link.href.replace(/https?:\/\/jellyseerr:5055/gi, buildTarget());
      if (updated !== link.href) {
        link.href = updated;
      }
    });
  };
  const observer = new MutationObserver(rewriteLinks);
  observer.observe(document.body, { childList: true, subtree: true });
  rewriteLinks();
})();
